CREATE TABLE users
(username VARCHAR(255) NOT NULL
, password VARCHAR(255) NULL
, CONSTRAINT PRIMARY KEY(username));
CREATE TABLE groups
(username VARCHAR(255) NULL
, groupname VARCHAR(255) NULL );
CREATE INDEX groups_users_FK1 ON groups(username ASC);
